$(document).ready(function(){$("#btn-go").click(function(){var e=$(this);e.button("loading");$("#success, #error, #progress, #btn-stop").hide();$("#progress-bar").removeClass("progress-bar-warning progress-bar-danger");var t=$("#select-grid-size").val();var n=new Worker("js/worker-compute.min.js");n.onmessage=function(n){var r=n.data;$("#success").html("There are <b>"+r.numPaths+"</b> "+"different paths to move from top left to bottom right in a "+"<b>"+t+"x"+t+"</b> grid without visitng the same square twice. "+"<br><br>Time taken to calculate this: <b>"+r.duration+"</b>").show();e.button("reset");$("#progress").hide();$("html, body").animate({scrollTop:$("#success").offset().top})};n.onerror=function(t){$("#error").html(["ERROR: Line ",t.lineno," in ",t.filename,": ",t.message].join("")).show();e.button("reset");$("#progress").hide();$("html, body").animate({scrollTop:$("#error").offset().top})};n.postMessage({gridSize:t});switch(t){case"6":$("#estimate").html("This will take around 5 minutes");$("#progress, #btn-stop").show();$("html, body").animate({scrollTop:$("#progress").offset().top});break;case"7":$("#progress-bar").addClass("progress-bar-warning");$("#estimate").html("This will take at least a few hours...");$("#progress, #btn-stop").show();$("html, body").animate({scrollTop:$("#progress").offset().top});break;case"8":$("#progress-bar").addClass("progress-bar-danger");$("#estimate").html("This will probably take forever. You've been warned.");$("#progress, #btn-stop").show();$("html, body").animate({scrollTop:$("#progress").offset().top});default:break}$("#btn-stop").click(function(){n.terminate();e.button("reset");$("#progress, #btn-stop").hide()})})})